import React, { useContext, useState } from "react";
import Radio from "@material-ui/core/Radio";
import RadioGroup from "@material-ui/core/RadioGroup";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import FormControl from "@material-ui/core/FormControl";
import FormLabel from "@material-ui/core/FormLabel";
import { Grid, Paper, makeStyles, Button } from "@material-ui/core";
import { useHistory } from "react-router-dom";
import { productContext } from "../contexts/ProductContext";
import Slider from "@material-ui/core/Slider";

const useStyles = makeStyles((theme) => ({
  paper: {
    padding: theme.spacing(2),
    color: theme.palette.text.secondary,
    marginRight: "20px",
    marginBottom: "20px",
    minWidth: "170px",
    maxWidth: "350px",
  },
}));

const Filter = () => {
  const history = useHistory();
  const classes = useStyles();
  const { getProducts } = useContext(productContext);
  const [type, setType] = useState(getType());
  const [price, setprice] = useState(getPrice());

  function getPrice() {
    const search = new URLSearchParams(history.location.search);
    return search.get("price_lte");
  }

  function getType() {
    const search = new URLSearchParams(history.location.search);
    return search.get("type");
  }

  const handleChangePrice = (event, value) => {
    const search = new URLSearchParams(history.location.search);
    search.set("price_lte", value);
    history.push(`${history.location.pathname}?${search.toString()}`);
    getProducts(history);
    setprice(value);
  };

  const handleChangeType = (event) => {
    if (event.target.value === "all") {
      history.push(`${history.location.pathname.replace("type")}`);
      getProducts(history);
      setType(event.target.value);
      return;
    }
    const search = new URLSearchParams(history.location.search);
    search.set("type", event.target.value);
    history.push(`${history.location.pathname}?${search.toString()}`);
    getProducts(history);
    setType(event.target.value);
  };

  const handleDrop = () => {
    history.push(`${history.location.pathname.replace("type")}`);
    history.push(`${history.location.pathname.replace("price_lte")}`);
    getProducts(history);
    setType(getType());
    setprice(getPrice());
  };

  return (
    <Grid item md={3}>
      <Paper
        elevation={2}
        className={classes.paper}
        style={{ marginTop: "80px" }}
      >
        <FormControl component="fieldset" style={{ color: "pink" }}>
          <FormLabel component="legend" color="pink">
            Type
          </FormLabel>
          <RadioGroup
            aria-label="gender"
            name="gender1"
            value={type}
            onChange={handleChangeType}
            style={{ color: "pink" }}
          >
            <FormControlLabel
              value="Hand Cream"
              control={<Radio />}
              label="Hand Cream"
            />
            <FormControlLabel
              value="Night Cream"
              control={<Radio />}
              label="Night Cream"
            />
            <FormControlLabel value="Tonic" control={<Radio />} label="Tonic" />
            <FormControlLabel value="Serum" control={<Radio />} label="Serum" />
            <FormControlLabel
              value="Face Mask"
              control={<Radio />}
              label="Face Mask"
            />
            <FormControlLabel
              value="Face Wash"
              control={<Radio />}
              label="Face Wash"
            />
          </RadioGroup>
        </FormControl>

        <Grid>
          <Slider
            value={price}
            onChange={handleChangePrice}
            aria-labelledby="discrete-slider"
            valueLabelDisplay="auto"
            min={10}
            max={500}
            style={{ color: "pink" }}
          />
          <Button
            variant="outlined"
            style={{ color: "pink" }}
            onClick={handleDrop}
          >
            Drop
          </Button>
        </Grid>
      </Paper>
    </Grid>
  );
};

export default Filter;
